mapper.select:
select c.*,u.name
from consumption c
left join user u on c.user_id = u.id


        c.user_id = #{userId}


    and c.event like concat ('%',#{event},'%')


    and u.name like concat ('%',#{username},'%')


        and c.amount >= #{minAmount}


        and c.amount <= #{maxAmount}


        and c.create_time >= #{beginDateTime}


        and c.create_time <= #{endDateTime}



order by c.create_time desc
