mapper.update:
UPDATE user
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, age = #{age}, phone = #{phone}, self_description = #{selfDescription}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
select id,username,name,gender,age,phone,self_description,create_time,update_time from user


        and name like concat('%',#{name},'%')


        and create_time > #{beginDateTime}


        and create_time < #{endDateTime}
